catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

		g_form.clearOptions('which_category_best_describes_your_request');
	var getRequesttype=g_form.getValue('is_this_req_related_to_info_security_compliance_and_the_submission_of_a_required_attestation_form');

	if(getRequesttype=='Yes')
		{
			g_form.clearOptions('which_category_best_describes_your_request');
			g_form.addOption('which_category_best_describes_your_request','Attestation Forms Questions and Requests','Attestation Forms Questions and Requests');
			g_form.addOption('which_category_best_describes_your_request','Attestation Submissions','Attestation Submissions');


		}
	else if(getRequesttype=='No')
		{
			g_form.clearOptions('which_category_best_describes_your_request');

				g_form.addOption('which_category_best_describes_your_request','Vendor Management','Vendor Management');
			g_form.addOption('which_category_best_describes_your_request','Compliance Auditing','Compliance Auditing');

		}


	else
		{
			g_form.clearOptions('which_category_best_describes_your_request');
		}


}
